jQuery(document).ready(function(){"use strict";


	$('.ira').on("click", function () {

		var altura_cabecera = $(".cabecera-nueva").height();
			
		var distaciahastaobjetivo = $(".aqui").offset().top - altura_cabecera;
			
		$('html, body').animate({scrollTop: distaciahastaobjetivo }, 800);
			return false;

	});
	




	//Añadir clase para personalizar mobile	
var widthWindow = $(window).width();
       
		if (widthWindow <= 463) {
		
			$('body').addClass('estoyenmobile');
			
        }
		else {
			$('body').removeClass('estoyenmobile');
		}
	
$(window).on('resize', function(event) {
        event.preventDefault();
        
	var widthWindow = $(window).width();
	
		if (widthWindow <= 463) {
		
			$('body').addClass('estoyenmobile');
			
        }
		else {
			$('body').removeClass('estoyenmobile');
		}
		
});
//Fin Añadir clase para personalizar mobile


//Ir hasta el mapita		

	if ($(".mapadespachos").length > 0) {
		
  
	var distancia_mapita = $(".mapadespachos").offset().top;

	$(".localizacion").not('.estoyenmobile .localizacion').click(function() {
        
	$('html, body').animate({scrollTop: distancia_mapita }, 800);
	return false;
	});
	
	var distancia_mapitamobiledos = $(".mapita img:last-of-type").offset().top - 30;

	$(".estoyenmobile .localizacion").click(function() {
        
	$('html, body').animate({scrollTop: distancia_mapitamobiledos }, 800);
	return false;
	});
	
	
		
		
     } 
	
//Ir hasta el mapita


//Ocultar bocadillo al hacer scroll en mobile
	
	if ($(".estoyenmobile").length > 0) {
		
	var porarriba = $('.globo.helper').offset().top - 50;
	
	
$(window).scroll(function() {
    if ( $(this).scrollTop() <= porarriba ) {
        $('.estoyenmobile .globo').not('.estoyenmobile .globo.helper').css('visibility','hidden');
    } 
	else{
		$('.estoyenmobile .globo').not('.estoyenmobile .globo.helper').css('visibility','visible');
	}
	
	var porabajo = $('.localizacion + img').offset().top;
	
	if ( $(this).scrollTop() >= porabajo ) {
        $('.estoyenmobile .globo').not('.estoyenmobile .globo.helper').css('visibility','hidden');
    } 
	

});
		
	}
//Fin Ocultar bocadillo al hacer scroll en mobile


 
  
 //Mostrar bocadillos puntos mapa
$(".localizacion").click(function() {
	$('.globo').not('.globo.helper').fadeOut(500);
    $('.globo', this).delay(500).fadeIn(500);
	$('.mapita .localizacion + img').css('opacity','0');
	$(this,'.mapita .localizacion').next().css('opacity','1');
	
	//modificar zindex geopuntos al abrir globo
	
    
	$('.row.mapita .localizacion').not(this).css({"z-index":"3"});
	$(this,'.row.mapita .localizacion').css({"z-index":"5","color":"#79A52D"});
	
    //modificar zindex geopuntos al abrir globo
	
});
$('.localizacion .globo .cerrar-globo, .localizacion .globo').click(function (e) {
    e.stopPropagation();
});
$(".globo .cerrar-globo, .globo .cerrar-help").click(function() {
    $(this,'.cerrar-globo').parent().fadeOut(500);
	$('.mapita .localizacion + img').css('opacity','0');
	
});	
//Fin Mostrar bocadillos puntos mapa 

//Activar elemento tabla y ver en el mapa	
$(".tabla-locations .tableta p").click(function() {

	
	
$(".tabla-locations .tableta p").removeClass('actived');
$(this,".tabla-locations .tableta p").addClass('actived');
	
$('.row.mapita .localizacion').css('color','#cc0033');
	
var claselementopinchado= $(this).attr('id');

$('.row.mapita .localizacion').not(this).css({"z-index":"3"});
$('.row.mapita .localizacion.'+claselementopinchado).css({"z-index":"5","color":"#79A52D"});
	
if($('body').hasClass('estoyenmobile')){
$('html, body').delay(500).animate({scrollTop: distancia_mapitamobiledos }, 800);}
	
else{
$('html, body').delay(500).animate({scrollTop: distancia_mapita }, 800);
}	

$('.globo').not('.globo.helper').fadeOut(500);	
$('.row.mapita .localizacion.'+claselementopinchado).children(".globo").delay(1300).fadeIn(300);
$('.mapita .localizacion + img').css('opacity','0');	
$('.row.mapita .localizacion.'+claselementopinchado).next("img").css('opacity','1');
	
return false;
	
});	
//Fin Activar elemento tabla	  

 

});