jQuery(document).ready(function(){"use strict";


	//Añadir clase para personalizar mobile	
var widthWindow = $(window).width();
       
		if (widthWindow <= 463) {
		
			$('body').addClass('estoyenmobile');
			
        }
		else {
			$('body').removeClass('estoyenmobile');
		}
	
$(window).on('resize', function(event) {
        event.preventDefault();
        
	var widthWindow = $(window).width();
	
		if (widthWindow <= 463) {
		
			$('body').addClass('estoyenmobile');
			
        }
		else {
			$('body').removeClass('estoyenmobile');
		}
		
});
//Fin Añadir clase para personalizar mobile

});

$(window).load(function() {
//Anclar boton al hacer scroll en mobile
	
	if ($(".estoyenmobile").length > 0) {
		
		var alturaboton = $('.pegable').height();
		
		var posicionboton = $('.pegable').offset().top;
				
		var alturaventana = $( window ).height();
		
		var visivilidadboton = posicionboton - alturaventana + alturaboton;		
		
		
			$(window).scroll(function() {
			
				
				
				
				if ( $(this).scrollTop() < visivilidadboton ) {
					
					$('.pegable').addClass('pegao');
		
				} 
				else{
					
					$('.pegable').removeClass('pegao');
				}


			});
		
	}
//Anclar boton al hacer scroll en mobile
});